import React, { useState } from 'react';
import axios from 'axios';

function App() {
    const [rootFolder, setRootFolder] = useState('');
    const [message, setMessage] = useState('');

    const handleMergePdfs = async () => {
        try {
            const response = await axios.post('http://localhost:8000/merge_pdfs', { root_folder: rootFolder });
            if (response && response.data) {
                setMessage(response.data.message);
            } else {
                setMessage('An unexpected error occurred.');
            }
        } catch (error) {
            if (error.response && error.response.data && error.response.data.detail) {
                setMessage('An error occurred: ' + error.response.data.detail);
            } else {
                setMessage('An unexpected error occurred.');
            }
        }
    };

    return (
        <div>
            <h1>PDF Merger</h1>
            <input
                type="text"
                placeholder="Enter the path to the root folder"
                value={rootFolder}
                onChange={(e) => setRootFolder(e.target.value)}
            />
            <button onClick={handleMergePdfs}>Merge PDFs</button>
            {message && <p>{message}</p>}
        </div>
    );
}

export default App;
